// src/components/Home.js
import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const Container = styled.div`
  max-width: 100%;
  font-family: Pretendard;

  h3 {
    color: #828282;
    font-size: 18px;
    font-style: normal;
    font-weight: 500;
    line-height: normal;
  }
`;

const Banner = styled.div`
  position: relative;
  text-align: center;
  height: 719px;
  background-image: url("/images/Home_bg.jpg");
  background-size: cover;
`;

const BannerContainer = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
  left: 84px;
  bottom: 20%;
  text-align: left;
`;

const Year = styled.span`
  color: #fff;
  font-family: Pretendard;
  font-size: 20px;
  font-style: normal;
  font-weight: 600;
  line-height: normal;
  margin-bottom: 29px;
`;

const Title = styled.h1`
  color: #fff;
  font-family: Pretendard;
  font-size: 65px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
  margin-bottom: 22px;
`;

const SubTitle = styled.span`
  color: #fff;
  font-family: Pretendard;
  font-size: 20px;
  font-style: normal;
  font-weight: 400;
  line-height: normal;
  margin-bottom: 29px;
`;

const OngSection = styled.div`
  padding: 67px 84px 0 84px;
  height: 585px;
`;

const StyledText = styled.span`
  font-size: 40px;
  font-weight: 700;
  font-style: normal;
  line-height: normal;
  color: ${(props) => props.color};
`;

const AboutSection = styled.div`
  padding: 0 84px;
  height: 675px;
  text-align: center;

  background-color: beige;
`;

const AwardSection = styled.div`
  padding: 0 84px;
  height: 150px;
  background-color: tomato;
`;

const ActivitySection = styled.div`
  padding: 0 84px;
  height: 1262px;
  background-color: oldlace;
`;

const SupportSection = styled.div``;

const Button = styled(Link)`
  display: inline-block;
  width: 196px;
  height: 50px;
  background-color: #00c3ff;
  color: white;
  text-align: center;
  line-height: 50px;
  text-decoration: none;
  border-radius: 5px;
  font-family: Pretendard;
  font-size: 15px;
  font-weight: 700;
`;

const Home = () => {
  return (
    <Container>
      <Banner>
        <BannerContainer>
          <Year>2024</Year>
          <Title>
            민족사관고등학교<br></br> 옹달샘
          </Title>
          <SubTitle>국내 유일 적정기술 해외봉사 동아리</SubTitle>
          <Button to="/about">더 알아보기</Button>
        </BannerContainer>
      </Banner>
      <OngSection>
        <StyledText color="#000">옹달샘의 특별한 </StyledText>
        <StyledText color="#00c3ff">봉사 이야기</StyledText>
        <h3>작은 노력이 모여 비로소 변화가 시작될 것이라 믿습니다.</h3>
      </OngSection>
      <AboutSection>
        <h3>작은 노력이 모여 비로소 변화가 시작될 것이라 믿습니다.</h3>
      </AboutSection>
      <AwardSection></AwardSection>
      <ActivitySection></ActivitySection>
      <SupportSection></SupportSection>
    </Container>
  );
};

export default Home;
