// src/components/Navbar.js
import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 84px;
  height: 100px; /* 네비게이션 바의 높이 */
  width: 100vw;
  box-sizing: border-box;

  background-color: #ffffff; /* 배경 색상 */
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); /* 약간의 그림자 효과 */
`;

const LogoSection = styled.div`
  display: flex;
  align-items: center;
`;

const Logo = styled.img`
  height: 40px; /* 로고 높이 설정 */
  margin-right: 10px; /* 로고와 회사명 사이 간격 */
`;

const CompanyName = styled.span`
  font-size: 1.5rem;
  font-weight: bold;
  color: #333333;
`;

const Menu = styled.div`
  display: flex;
  gap: 24px; /* 각 메뉴 간격 */
`;

const MenuItem = styled.a`
  font-size: 1rem;
  text-decoration: none;
  color: #333333;
  padding: 8px 0;
  transition: color 0.3s ease;

  &:hover {
    color: #007bff; /* 호버 시 색상 변경 */
  }
`;

const SupportButton = styled.a`
  padding: 10px 20px;
  background-color: #ff5722;
  color: #ffffff;
  font-size: 1rem;
  text-decoration: none;
  border-radius: 4px;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #e64a19; /* 호버 시 버튼 색상 변경 */
  }
`;

const Navbar = () => {
  return (
    <Nav>
      <LogoSection>
        {/* <Logo src="/images/logo.png" alt="Company Logo" /> */}
        <CompanyName>My Company</CompanyName>
      </LogoSection>
      <Menu>
        <MenuItem to="/about">소개</MenuItem>
        <MenuItem to="#activities">활동</MenuItem>
        <MenuItem to="#story">스토리</MenuItem>
        <MenuItem to="#technology">기술</MenuItem>
        <MenuItem to="#team">구성원</MenuItem>
      </Menu>
      <SupportButton to="#support">후원하기</SupportButton>
    </Nav>
  );
};

export default Navbar;
